#include <queue>
#include <memory>
#include <ctime>
#include "Patient.h"
#include "PatientRecord.h"
#include "app.h"


int main () {
    std::priority_queue<
        PatientRecord,
        std::vector<PatientRecord>,
        PatientComparator
    > erQueue;

    auto p1 = std::make_shared<Patient>("Alice", 30, 2, "Arm pain");
    auto p2 = std::make_shared<Patient>("Bob", 45, 8, "Chest pain");

    while (!erQueue.empty()) {
        auto next = erQueue.top();
        next.patient->displayInfo();
        erQueue.pop();
    }


    return 0;
}
